package javainterviewcodequestions;

public class Yildiz {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		
		for (int i = 5; i > 0; i--) {
			for (int j = i; j > 0; j--) {
				System.out.print("*");
			}
			System.out.println();
		}
		
		
		for (int i = 0; i < 5; i++) {
			for (int j = i; j < 5; j++) {
				System.out.print(j+1+" ");
			}
			System.out.println();
		}
		
		
		
	}

}
